#~/bin/bash

[ -z "$1" ] && echo "usange ./$0 <dataset size in MB>" && exit 1

# arg1 is size of db in MB
size=$1

itr=$((size*250))

rm benchmark_tbl.ini > /dev/null 2>&1
echo "generatin a db file for size=$size MB with iteration=$itr"
for i in `seq 1 $itr`;
do
        echo "$i,\"\"" >> benchmark_tbl.ini
done


